//C的AC
string gou(int l,int zhi)
{
	string q="";
	//cout<<l<<" "<<zhi<<"@"<<endl;
	while(zhi)
	{
		if(zhi%2)q=q+"1";
		else q=q+"0";
		zhi/=2;
		l--;
	}
	while(l)
	{
		q="0"+q;
		l--;
	}
	return q;
}
string cun;
void ping(string s)
{
	string ss=cun+s;
	int ans=0;
	int l=ss.length(),b=0;//b是指针
	while(b+8<l)
	{
		for(int i=b;i<b+8;i++)
		{
			ans*=2;
			if(ss[i]=='1')ans+=1;
		}
		p[tot++]=ans;
		ans=0;
		b+=8;
	}
	cun=ss.substr(b);
}
